import { USER_SUBMIT } from '../actions/actionTypes';

const INITIAL_STATE = {
  user: {
    email: '', // string que armazena o email da pessoa usuária
  },
};

const user = (state, action) => {
  switch (action.type) {
  case USER_SUBMIT:
    return {
      ...state,
      email: action.payload.email,
    };
  default:
    return state;
  }
};

export default user;
