import { USER_LOGIN, PLAYER_SCORE } from '../actions';

const INITIAL_STATE = {
  nameImput: '',
  emailImput: '',
  score: 0,
};

function user(state = INITIAL_STATE, action) {
  switch (action.type) {
  case USER_LOGIN:
    return { ...state,
      nameImput: action.payload.nameImput,
      emailImput: action.payload.emailImput,
    };
  case PLAYER_SCORE:
    return { ...state,
      score: action.payload,
    };
  default:
    return state;
  }
}

export default user;
