import PropTypes from 'prop-types';
import React, { Component } from 'react';
import { connect } from 'react-redux';
import md5 from 'crypto-js/md5';

class Header extends Component {
  render() {
    const { nameImput, emailImput, score } = this.props;
    const hash = md5(emailImput).toString();
    return (
      <div>
        <img
          src={ `https://www.gravatar.com/avatar/${hash}` }
          alt={ nameImput }
          data-testid="header-profile-picture"
        />
        <h2 data-testid="header-player-name">{ nameImput }</h2>
        <h4 data-testid="header-score">{ score }</h4>
      </div>
    );
  }
}

Header.propTypes = {
  emailImput: PropTypes.string,
  nameImput: PropTypes.string,
  score: PropTypes.string,
}.isRequired;

const mapStateToProps = (globalState) => ({
  nameImput: globalState.player.nameImput,
  emailImput: globalState.player.emailImput,
  score: globalState.player.score,
});

export default connect(mapStateToProps)(Header);
