import React, { useContext, useState } from 'react';
import homeContext from '../context/homeContext';

function FormNumberFilter() {
  const [state, setState] = useState({
    column: 'population',
    comparison: 'maior que',
    value: 0,
  });
  const { handleClick } = useContext(homeContext);

  return (
    <div className="filter-container">
      <select
        name="column"
        data-testid="column-filter"
        value={ state.column }
        onChange={ ({ target }) => setState({ ...state, column: target.value }) }
      >
        <option>population</option>
        <option>orbital_period</option>
        <option>diameter</option>
        <option>rotation_period</option>
        <option>surface_water</option>
      </select>
      <select
        name="comparison"
        data-testid="comparison-filter"
        value={ state.comparison }
        onChange={ ({ target }) => setState({ ...state, comparison: target.value }) }
      >
        <option>maior que</option>
        <option>menor que</option>
        <option>igual a</option>
      </select>
      <input
        type="number"
        name="value"
        data-testid="value-filter"
        value={ state.value }
        onChange={ ({ target }) => setState({ ...state, value: target.value }) }
      />
      <button
        type="button"
        data-testid="button-filter"
        onClick={ () => handleClick(state) }
      >
        Filtrar

      </button>
    </div>
  );
}

export default FormNumberFilter;
