import { useContext } from 'react';
import ApiContext from '../context/ApiContext';

function NameFilter() {
  const { nameFilter } = useContext(ApiContext);
  return (
    <label>
      Pesquise um Planeta
      <input
        type="text"
        name="nameFilter"
        data-testid="name-filter"
        onChange={ ({ target }) => nameFilter(target.value) }
      />
    </label>
  );
}

export default NameFilter;
