import React, { useContext } from 'react';
import { DataContext } from '../../context/DataContext';

function FilterInput() {
  const { handleFilter, state, setState, columns, setColumns } = useContext(DataContext);

  return (
    <div>
      <label htmlFor="search-input">
        Pesquise
        <input
          id="search-input"
          type="text"
          name="search"
          onChange={ ({ target }) => handleFilter('nameFilter', target.value) }
          data-testid="name-filter"
        />
      </label>
      <div>
        <label htmlFor="filter-by-column">
          Coluna
          <select
            id="filter-by-column"
            name="column"
            data-testid="column-filter"
            value={ state.column }
            onChange={ ({ target }) => setState({ ...state, column: target.value }) }
          >
            {columns.map((column) => (
              <option key={ column } value={ column }>
                {column}
              </option>
            ))}
          </select>
        </label>
        <label htmlFor="filter-by-comparison">
          Comparação
          <select
            id="filter-by-comparison"
            name="comparison"
            data-testid="comparison-filter"
            value={ state.comparison }
            onChange={ ({ target }) => setState({ ...state, comparison: target.value }) }
          >
            <option value="maior que">maior que</option>
            <option value="menor que">menor que</option>
            <option value="igual a">igual a</option>
          </select>
        </label>
        <label htmlFor="filter-by-value">
          Valor
          <input
            id="filter-by-value"
            type="number"
            name="value"
            data-testid="value-filter"
            value={ state.value }
            onChange={ ({ target }) => setState({ ...state, value: target.value }) }
          />
        </label>
        <button
          type="button"
          data-testid="button-filter"
          onClick={ () => {
            handleFilter('btnFilter', state);
            setColumns(columns.filter((column) => column !== state.column));
            setState({
              column: 'population',
              comparison: 'maior que',
              value: 0,
            });
          } }
        >
          Filtrar
        </button>
      </div>
    </div>
  );
}

export default FilterInput;
