import PropTypes from 'prop-types';
import { useEffect, useState } from 'react';
import ApiContext from './ApiContext';

export default function ApiProvider({ children }) {
  const [planets, setPlanets] = useState([]);
  const [filtredPlanets, setFiltredPlanets] = useState([]);

  useEffect(() => {
    async function fetchData() {
      const response = await fetch('https://swapi.dev/api/planets');
      const data = await response.json();
      const planetsData = data.results.map((planet) => {
        delete planet.residents;
        return planet;
      });
      setPlanets(planetsData);
      setFiltredPlanets(planetsData);
    }
    fetchData();
  }, []);

  function nameFilter(text) {
    const regex = new RegExp(`${text}`, 'i');
    setFiltredPlanets(planets.filter((item) => item.name.match(regex)));
  }
  return (
    <ApiContext.Provider value={ { filtredPlanets, nameFilter } }>
      {children}
    </ApiContext.Provider>
  );
}
ApiProvider.propTypes = {
  children: PropTypes.node.isRequired,
};
