import React from 'react';
import copy from 'clipboard-copy';
import RecipeContext from '../context/RecipeContext';
import Footer from '../components/Footer';
import Header from '../components/Header';
import Share from '../images/shareIcon.svg';

function DoneRecipes() {
  const doneRecipes = JSON.parse(localStorage.getItem('doneRecipes')) || [];
  console.log(doneRecipes);
  const { recipe } = useContext(RecipeContext);

  const handleShare = (event) => {
    event.preventDefault();
    const time = 2000;
    const baseUrl = 'http://localhost:3000';
    const url = doneRecipes.idMeal
      ? `${baseUrl}/meals/${recipe.idMeal}`
      : `${baseUrl}/drinks/${recipe.idDrink}`;
    copy(url);
    console.log(url);
    const sucessMEssage = document.getElementById('sucess-message');
    sucessMEssage.style.display = 'block';

    setTimeout(() => {
      sucessMEssage.style.display = 'none';
    }, time);
  };

  return (
    <div>
      <h1>DoneRecipes</h1>
      <Header />
      <div>
        <button data-testid="filter-by-all-btn">All</button>
        <button data-testid="filter-by-meal-btn">Meals</button>
        <button data-testid="filter-by-drink-btn">Drinks</button>
      </div>
      <div>
        {doneRecipes.map((recipe, index) => (
          <div key={ index }>
            <img
              data-testid={ `${index}-horizontal-image` }
              src={ recipe.image }
              alt="recipe"
            />
            <p
              data-testid={ `${index}-horizontal-top-text` }
            >
              {`${recipe.nationality} - ${recipe.category}`}

            </p>
            <h2 data-testid={ `${index}-horizontal-name` }>{recipe.name}</h2>
            <p data-testid={ `${index}-horizontal-done-date` }>{recipe.doneDate}</p>
            <button
              data-testid={ `${index}-horizontal-share-btn` }
              onClick={ (event) => handleShare(event, recipe) }
            >
              <img src={ Share } alt="share icon" />
            </button>
            <div id="sucess-message" style={ { display: 'none' } }>Link copied!</div>
            {recipe.tags ? recipe.tags.map((tag, i) => {
              console.log(tag); // <--- console.log adicionado aqui
              return (
                <span
                  key={ i }
                  data-testid={ `${index}-${tag}-horizontal-tag` }
                >
                  {tag}
                </span>
              );
            }) : null}
          </div>
        ))}
      </div>
      <Footer />
    </div>
  );
}

export default DoneRecipes;
