import React, { useState } from 'react';

function RecipeInProgress() {
  const [ingredients, setIngredients] = useState([]);

  const handleStartRecipes = () => {
    fetch('https://www.themealdb.com/api/json/v1/1/search.php?s=')
      .then((response) => response.json())
      .then((data) => {
        const recipeIngredients = data.meals[0].strIngredient.map((ingredient) => ({
          name: ingredient,
          checked: false,
        }));
        setIngredients(recipeIngredients);
      });
  };

  const handleIngredientChange = (index) => {
    const newIngredients = [...ingredients];
    newIngredients[index].checked = !newIngredients[index].checked;
    setIngredients(newIngredients);
  };

  return (
    <div>
      <button onClick={ handleStartRecipes }>Start Recipes</button>

      {ingredients.map((ingredient, index) => (
        <label key={ index } htmlFor={ `${index}-ingredient-step` }>
          <input
            type="checkbox"
            id={ `${index}-ingredient-step` }
            data-testid={ `${index}-ingredient-step` }
            checked={ ingredient.checked }
            onChange={ () => handleIngredientChange(index) }
          />
          {ingredient.name}
        </label>
      ))}

      <button data-testid="finish-recipe-btn" name="finish" type="button">
        Finalizar
      </button>
    </div>
  );
}

export default RecipeInProgress;
