const Joi = require('joi');

const addProductSchema = Joi.object({
  name: Joi.string().min(5).required(),
});

const addSaleSchema = Joi.array().items(Joi.object({
  productId: Joi.number().integer().required().messages({
    'any.required': '"productId" is required',
  }),
  quantity: Joi.number().min(1).required().messages({
    'any.required': '"quantity" is required',
    'number.min': '"quantity" must be greater than or equal to 1',
  }),
}));

module.exports = {
  addProductSchema,
  addSaleSchema,
};

src/utils/generateFormattedQuery.js.source:
const snakeize = require('snakeize');

const getFormattedColumnNames = (object) => Object.keys(snakeize(object)).join(',');

const getFormattedPlaceholders = (object) => Object.keys(object).map(() => '?').join(',');

const getFormattedUpdateColumns = (object) => Object.keys(snakeize(object))
  .map((key) => `${key} = ?`)
  .join(', ');

module.exports = {
  getFormattedColumnNames,
  getFormattedPlaceholders,
  getFormattedUpdateColumns,
};

src/utils/mapHttpStatus.js.source:
const httpStatusCode = {
  SUCCESSFUL: 200,
  NOT_FOUND: 404,
  CREATED: 201,
  MISSING_VALUES: 400,
  INVALID_VALUES: 422,
};

const mapHttpStatus = (status) => httpStatusCode[status] || 500;

module.exports = mapHttpStatus;

